import React from 'react';
import {Container, Row, Col} from 'react-bootstrap';
import '../App.css';

const Login = (props) => {
    return(
        <Container className="center">
            <Row style={{marginBottom:"2%"}}>
                <Col>
                    <input placeholder="username" onChange={(event) => props.credentials("username", event.target.value)}></input>
                </Col>
            </Row>
            <Row style={{marginBottom:"2%"}}>
                <Col>
                    <input placeholder="password"></input>
                </Col>
            </Row>
            <Row style={{marginBottom:"2%"}}>
                <Col>
                    <button style={{width:"80%"}}>Sign In</button>
                </Col>
            </Row>
        </Container>
    )
}

export default Login;
