import React from 'react';
import Login from './components/login';
import HomeScreen from './components/homeScreen';
import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      username: null,
      password: null,
      signedIn: false,
      error: null,
      errorMessage: null
    }
  }

  signIn = async () => {
    const token = await axios.get(`http://localhost:7000/login?username=${this.state.username}&password=${this.state.password}`);
    if(!token.data.auth) {
      this.setState({error: true, errorMessage: token.data.message});
      sessionStorage.setItem('token', token);
      return;
    }

    const validateToken = await axios.get('http://localhost:7000/validate', {
      headers: {
        'x-access-token': token.data.token
      }
    });

    if(validateToken.status === 200) {
      this.setState({signedIn: true});
    }
  }

  componentDidMount () {
    const token = sessionStorage.getItem('token');
    const validateToken = axios.get('http://localhost:7000/validate', {
      headers: {
        'x-access-token': token
      }
    }).then(() => {
      if(validateToken.status === 200) {
        this.setState({signedIn: true});
      } else {
        this.setState({signedIn: false});
      }
    })
  }

  render() {
    return (
      <div className="App">
        {!this.state.signedIn ? <Login errorMessage={this.state.errorMessage} credentials={(x, y) => {this.setState({[x]: y})}} getSignIn={() => this.signIn()}/> : <HomeScreen/>}
      </div>
    );
  }
}

export default App;
